import React from 'react';
import { useState } from 'react';
import {Form, Button , Container } from 'react';
import axios from 'axios';


function Create() {

    const [ brand, setBrand ] = useState();
    const [ name, setName ] = useState();
    const [ size, setSize ] = useState();
    const [ price, setPrice ] = useState();

    // const handleChange = (e) => {
    //     const { name, value } = e.target;
    //     setFormData((prevData) => ({
    //         ...prevData,
    //         name: value,
    //     }));
    // };

    const handleSubmit = () => {
        const payload = {
            brand: brand,
            name: name,
            size: size,
            price: price
        }
        // axios.post('http://localhost:5000/api/addclothes/', payload);
        try {
            const response = axios.post('http://localhost:5000/api/addclothes/', payload);
            console.log('Clothes data submitted', response.data);
        } catch (error) {
            console.error('Error submitting clothes data')
        }
    };


    // const getBrand = (e) => {
    //     let value = e.target.value;
    //     setBrand(value);
    // };
    // const getName = (e) => {
    //     let value = e.target.value;
    //     setName(value);
    // };
    // const getSize = (e) => {
    //     let value = e.target.value;
    //     setSize(value);
    // };
    // const getPrice = (e) => {
    //     let value = e.target.value;
    //     setPrice(value);
    // }; 

  return (
    <div>
        <Container>
            <Form onSubmit ={handleSubmit}>
                <Form.Group controlId='brand'>
                    <Form.Label>Brand</Form.Label>
                    <Form.Control
                        type='text'
                        name='brand'
                        value={brand}
                        onCgange={(e) => setBrand(e.target.value)}
                        required
                    />
                </Form.Group>
                <Form.Group controlId='name'>
                    <Form.Label>Name</Form.Label>
                    <Form.Control
                        type='text'
                        name='name'
                        value={name}
                        onCgange={(e) => setName(e.target.value)}
                        required
                    />
                </Form.Group>
                <Form.Group controlId='size'>
                    <Form.Label>Size</Form.Label>
                    <Form.Control
                        type='text'
                        name='size'
                        value={size}
                        onCgange={(e) => setSize(e.target.value)}
                        required
                    />
                </Form.Group>
                <Form.Group controlId='price'>
                    <Form.Label>Price</Form.Label>
                    <Form.Control
                        type='text'
                        name='price'
                        value={price}
                        onCgange={(e) => setPrice(e.target.value)}
                        required
                    />
                </Form.Group>
            </Form>
        </Container>

    </div>
  )
}

export default Create
