import React from 'react'
import { Card, Button } from 'react-bootstrap';
import axios from 'axios';

const ProductCard = ({ clothes }) => {
    console.log(clothes);
    const handleDelete = (clothesID) => {
        axios.delete('http://localhost:5000/api/getclothes' + clothesID)
    };

    return (
    <Card className='shadow' style={{width: '18rem'}}>
        <Card.Body>
            <Card.Title>{clothes.brand}</Card.Title>
            <Card.Subtitle className='mb-2 text-muted'>{clothes.name}</Card.Subtitle>
            <Card.Text>
                <strong>Size:</strong>{clothes.size}<br/>
                <strong>Price:</strong>{clothes.price}<br/>
                <strong>Quantity:</strong>{clothes.quantity}<br/>
            </Card.Text>
            <Button variant = 'danger' onClick={() => handleDelete(clothes.id)}>Delete</Button>
        </Card.Body>
    </Card>
  );
};
