import './App.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useState } from 'react';
import Header from './Header Component/Component-Header'; // Correct component paths
import Navbar from './Navbar Component/Component-Navbar'; // Correct component paths
import Footer from './Footer Component/Component-Footer'; // Correct component paths
import Landing from './Pages/landing';
import Products from './Pages/products';
import Checkout from './Pages/checkout';
import Login from './Pages/login';
import Admin from './Pages/admin';
import Register from './Pages/register';

function App() {
  const [product, setProduct] = useState({
    Brand: '',
    Name: '',
    Size: '',
    Price: '',
    Description: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProduct((prevProduct) => ({
      ...prevProduct,
      [name]: value,
    }));
  };

  return (
    <div className="App">
      <Router>
        <div className='header'>
          <Navbar />
        </div>
        <br />
        <div className='carousel'>
          <Header />
        </div>
        <div className='page'>
          <Routes>
            <Route path='../Pages/landing' element={<Landing />} />
            <Route path='/products' element={<Products />} />
            <Route path='/checkout' element={<Checkout />} />
            <Route path='/login' element={<Login />} />
            <Route path='/admin' element={<Admin />} />
          </Routes>
        </div>
        <div className='foot'>
          <Footer />
        </div>
      </Router>
    </div>
  );
}

export default App;
