import React from 'react';
import { useState } from 'react';
import '../App.css';

function Checkout() {
    // Sample data for items in cart
    const [cartItems, setCartItems] = useState([
        { id: 1, name: 'Item 1', price: 10.99, thumbnail: 'url_to_item1_image.jpg' },
        { id: 2, name: 'Item 2', price: 5.49, thumbnail: 'url_to_item2_image.jpg' },
        { id: 3, name: 'Worker chino relaxed', price: 800, thumbnail: 'src/Components/NewProductsComponent/images/Worker chino relaxed - dk black.jpg' },
        { id: 4, name: 'Worker chino relaxed', price: 350, thumbnail: 'src/Components/NewProductsComponent/images/Worker chino relaxed - salute.jpg' },
        { id: 5, name: 'Acrylic-Knitted-Trousers', price: 2000 },
        { id: 6, name: 'Street track pant', price: 1100 },
        { id: 7, name: 'Taupe Shacket', price: 1900 },
        { id: 8, name: 'TS Dynamic Puffer Gold Jacket', price: 2900 },
        { id: 9, name: 'Taupe Smart Suede Aviator Jacket', price: 2000 },
        { id: 10, name: 'Terracotta Corduroy Relaxed Shirt', price: 400 },
        // ... add more items as needed 
    ]);

    // Calculate total
    const calculateTotal = () => {
        return cartItems.reduce((acc, item) => acc + (item.price || 0), 0).toFixed(2);
    };

    const handleRemoveItem = (itemId) => {
        // Remove the item from the cart based on its ID
        const updatedCart = cartItems.filter(item => item.id !== itemId);
        setCartItems(updatedCart);
    };

    const handleBuyClick = () => {
        // Here you'd handle the purchase process, e.g., calling an API
        alert('Purchase completed!');
    };

    return (
        <div className='checkoutPage'>
            <h1>Checkout</h1>
            <div className='form'>
                <ul>
                    {cartItems.map(item => (
                        <li key={item.id} style={{ display: 'flex', alignItems: 'center' }}>
                            {item.thumbnail && <img src={item.thumbnail} alt={item.name} className='thumbnail' />}
                            <div>
                                {item.name} - ${item.price ? item.price.toFixed(2) : 'Price not specified'}
                            </div>
                            <button className='remove' onClick={() => handleRemoveItem(item.id)}>Remove</button>
                        </li>
                    ))}
                </ul>
                <h3>Total: ${calculateTotal()}</h3>
                <button onClick={handleBuyClick}>Buy</button>
            </div>
        </div>
    );
}

export default Checkout;

