import React from 'react';
import { useState } from 'react';
import '../App.css';

function Checkout({ cartItems, handleRemoveItem }) {
    // Calculate total
    let total = 0;
  
    for (const item of cartItems) {
      const price = parseFloat(item.price.replace(/[^\d.]/g, ''));
      if (!isNaN(price)) {
        total += price;
      }
    }
  
    const formattedTotal = total.toFixed(2);
  
    const handleBuyClick = () => {
      // Here you'd handle the purchase process, e.g., calling an API
      alert('Purchase completed!');
    };
  
    return (
      <div className='checkoutPage'>
        <h1>Checkout</h1>
        <div className='form'>
          <ul>
            {cartItems.map((item) => (
              <li key={item._id} style={{ height: '60px', display: 'flex', alignItems: 'center' }}>
                <div>
                  {item.name} - {item.price}
                </div>
                <button className='remove' onClick={() => handleRemoveItem(item._id)}>Remove</button>
              </li>
            ))}
          </ul>
          <h3>Total: ${formattedTotal}</h3>
          <button onClick={handleBuyClick}>Buy</button>
        </div>
      </div>
    );
  }
  
  export default Checkout;


